node.code:
my ($limit) = @_;
$limit ||= 15;

#return "This is a test of the \"fix up expensive code\" system. This is only a #test. Back in 20-30 -jaybonci" unless $USER->{title} eq "JayBonci";
if(!exists($HTMLVARS{'group_gods'}{$$USER{node_id}}) && !exists($HTMLVARS{'group_content editors'}{$$USER{node_id}})){  my $blah = getNodeById(1217114); return $$blah{nltext};}
#we need this for now
#1217114 - hardcoded New Writeups Normal nodelet

my $UID = $$USER{user_id} || 0;
$limit = $$VARS{num_newwus} if($$VARS{num_newwus});

my $isRoot = exists($HTMLVARS{'group_gods'}{$UID}) ;

my $cansee = ($isRoot || exists($HTMLVARS{'group_content editors'}{$UID}));

my $nr = 855769; #hard-coded node row superdoc
my $str = '<table width="100%" border="0" cellpadding="0" cellspacing="0">';

my $count=0;
my @colors;
@colors = split(/\s*,\s*/,$$THEME{themeColors}) if exists $$THEME{themeColors};
@colors = ('#CCCC99') unless @colors;

my $nwf = getNodeById(1801407); # hard-coded Test NW Feeder nodelet (ktj's revised feeder)
my @wucache = eval('my '.$$nwf{nltext});
my $found  = 0;
my $iter = 0;

if ($cansee) {
  # Admins can suppress display of hidden/deleted writeups - LB 29Apr06
  if ($query->param("gochange")) { # Only swap on a "show" command from NW
    if ($query->param("nw_nojunk")) {
#      $VARS->{nw_nojunk} = 1;
       $$VARS{nw_nojunk} = 1;
    } else {
#      $VARS->{nw_nojunk} = 0;
      $$VARS{nw_nojunk} = 0;
    }
  }
}

while($found < $limit) {
#	$iter++; >>> Causes the miss-most-recent display bug.
	my $N = $wucache[0][$iter];
	last if $N==undef;	#possible since hidden things are skipped
	$iter++;

	if($$N{notnew}) {
		next unless ($cansee or $UID==$$N{author_user});
                # Suppress hidden/deleted w/us, if so selected - LB 29Apr06
                next if $$VARS{nw_nojunk};
	} else {
		++$found;
	}

	my $clr = $colors[$count++%int(@colors)];

	$str .= '<tr bgcolor="'.$clr.'"><td class="oddrow author" align="center" colspan="2"><strong>'.linkNode($$N{author_user}, '', {lastnode_id=>undef});

	if($cansee) {
		$str .='&nbsp;&nbsp;&nbsp;&nbsp;';
                if($$N{reputation}<0) {
                  $str .= 'R:'.$$N{reputation}.' ';
                }
		if($$N{notnew}){
		  $str .= '(<font color="red">H!</font>)';
                $str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'unhidewriteup', 'hidewriteup'=>$$N{writeup_id}}).' class="hide">un-h!</a>)';                }else{
		$str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'hidewriteup', 'hidewriteup'=>$$N{writeup_id}}).' class="hide">h?</a>)';
                }
	$str .= '(<font color="red">...</font>)' if $DB->sqlSelect('notnew', 'newwriteup', "node_id=$$N{writeup_id}") != $$N{notnew};        
        $str .=' (X)' if $DB->sqlSelect('count(*)', 'weblog', "weblog_id=$nr and to_node=$$N{writeup_id}");
	}



	$str .='</strong></td>';

#if ($$USER{node_id} == 1312198) {
#	$timeStr='E - '.$timeCount.': ' . Time::HiRes::tv_interval(\@start, [ #Time::HiRes::gettimeofday ]) . ' s<br />';
#	$timeCount++;
#}

	$str .= '</tr><tr><td align="left" class="title">'.linkNode($$N{parent_e2node}, $$N{parent_title}, {lastnode_id=>undef}).'</td><td align="right" class="type"><small>('.linkNode($$N{writeup_id}, $$N{type_title},{lastnode_id=>undef}).') </small>';
	$str.= "</td></tr>\n";

}
$str.='</font></td></tr></table>';
$str .= parseLinks('<p>([ENN|Older stuff])</p>');

return $str;
