node.code:
my $limit = $$VARS{num_newwus} || 15;

#if(!exists($HTMLVARS{'group_gods'}{$$USER{node_id}}) && !exists($HTMLVARS{'group_content editors'}{$$USER{node_id}})){  my $blah = getNodeById(1217114); return $$blah{nltext};}

my $UID = $$USER{user_id} || 0;
my $isRoot = exists($HTMLVARS{'group_gods'}{$UID}) ;
my $cansee = ($isRoot || exists($HTMLVARS{'group_content editors'}{$UID}));

my $str = '<ul id="writeup_list">';

my $nwf = getNodeById(1801407); # hard-coded Test NW Feeder nodelet (ktj's revised feeder)
my @wucache = eval('my '.$$nwf{nltext});
my ($found, $count, $iter)  = 0;


if ($cansee) {
  # Admins can suppress display of hidden/deleted writeups - LB 29Apr06
  if ($query->param("gochange")) { # Only swap on a "show" command from NW
    if ($query->param("nw_nojunk")) {
      $$VARS{nw_nojunk} = 1;
    } else {
      $$VARS{nw_nojunk} = 0;
    }
  }
}

while($found < $limit) {
#	$iter++; >>> Causes the miss-most-recent display bug.
	my $N = $wucache[0][$iter];
	last if $N==undef;	#possible since hidden things are skipped
	$iter++;

	if($$N{notnew}) {
		next unless ($cansee); # had "or $UID==$$N{author_user}" in condition, but historically users don't see their hidden writeups -2s, 18Jul07
                # Suppress hidden/deleted w/us, if so selected - LB 29Apr06
                next if $$VARS{nw_nojunk};
	} else {
		++$found;
	}

	$str .= "\n<li>\n\t<span class='writeupmeta".($$N{notnew} ? ' wu_hide' : '')."'>\n\t\t<span class='author'>".linkNode($$N{author_user}, '', {lastnode_id=>undef}).'</span>';

	if($cansee) {
                if($$N{reputation}<0) {
                  $str .= 'R:'.$$N{reputation}.' ';
                }
		if($$N{notnew}){
			$str .= "\n\t\t<span class='hidden'>(H!)</span>";
	                $str .= "\n\t\t<span class='hide'>(<a href=".urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'unhidewriteup', 'hidewriteup'=>$$N{writeup_id}}).'>un-h!</a>)</span>';
		}
		else{
			$str .= "\n\t\t<span class='hide'>(<a href=".urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'hidewriteup', 'hidewriteup'=>$$N{writeup_id}}).'>h?</a>)</span>';
                }
		$str .= "\n\t\t<span class='hidden'>(...)</span>" if $DB->sqlSelect('notnew', 'newwriteup', "node_id=$$N{writeup_id}") != $$N{notnew};        
	        $str .=' (X)' if $DB->sqlSelect('count(*)', 'weblog', "weblog_id=855769 and to_node=$$N{writeup_id} limit 1"); # 855769 - node row hardcoded
	}



	$str .= "\n\t\t<span class='title'><a href='".urlGenNoParams(getNode($$N{parent_e2node}),1)."#node_id_".$$N{writeup_id}."'>".$$N{parent_title}."</a></span>
\t\t<span class='type'>(".linkNode($$N{writeup_id}, $$N{type_title},{lastnode_id=>undef}).")</span>
\t</span>";
	$str.= "\n</li>";

}

$str.='</ul>';

return $str;
