node.page:
[%
my $restrict;
if ( $query->param( 'sexisgood' ) ) {

    $restrict = $query->param( 'debatecomment_restricted' );
    $$NODE{ 'restricted' } = $restrict;
}
$restrict = getNodeById($$NODE{ 'root_debatecomment' }) -> {'restricted'};
my $title = $$NODE { 'title' };
$title =~ s/\</\&lt\;/g;
$title =~ s/\>/\&gt\;/g;

my $ug_name = getNodeById($restrict) -> {'title'};

$title =~ /^\s*([\w\s]+):/;
my $prefix = $1;

$title = "$ug_name: ".$title unless lc($prefix) eq lc($ug_name);

$$NODE { 'title' } = $title;


updateNode( $NODE, $USER );

'';
%]

[{showdebate:0}]

[%
my $rootnode = getNodeById( $$NODE{ 'root_debatecomment' } );
my $restrict = $$NODE{restricted};

# If the user's not in the usergroup, deny access
if ( ! $APP->inUsergroup($USER, getNodeById($$rootnode{ restricted })) && 
     ! Everything::isApproved($USER,$rootnode) ) {
    return '';
}

my $str = '';

$str .= '
<input type="hidden" name="debatecomment_author_user" value="' . $$USER{ 'node_id' } . '"></input>
<input type="hidden" name="sexisgood">
<input type="hidden" name="debatecomment_restricted" value="' . $restrict . '">
<h2>Edit your comment</h2>
<p>
<label>Comment title:';

my $ug_id = $restrict;
my $ug = getNodeById($ug_id);
my $ug_name = $$ug{'title'};
my $cleantitle = $$NODE{ 'title' };
$cleantitle =~ s/^$ug_name\: //;

$cleantitle =~ s/\&lt\;/\</g;
$cleantitle =~ s/\&gt\;/\>/g;

my $fieldname = $$NODE{type}{title}."_title";

$str .= $query -> textfield($fieldname,$cleantitle, 20);
'</label>
</p>
';

$str .= '<p><strong>Comment:</strong><br>
[{textarea:doctext,20,60,virtual}]</p>';

return parseCode( $str );
%]
