node.page:
[{votehead}]
<table width=100% cellpadding=1 cellspacing=1>

[%
my $index = $query->param("index") if $query->param("index");
$index ||=0;

my @group = @{ $$NODE{group} } if $$NODE{group};
my @prevnodes = @group [0..$index-1] if $index > 1;
my @nextnodes = @group [$index+2..@group-1] if $index+2 < @group;

my $str;
$str.="<TR><td width=10% valign=top align=left>";


my $prevbox = "";


foreach (@prevnodes){
  getRef $_;
  my $WRTYPE=getNode $$_{wrtype_writeuptype};
  $prevbox.="(".linkNode($_, $$WRTYPE{title})
    .")&nbsp;by&nbsp"
    .linkNode($$_{author_user})."<br>";
}
$prevbox="<font size=2>$prevbox</font>" if $prevbox;
$prevbox.="<p>"
  .linkNode($NODE, "previous", {displaytype=>'cylinder', 
  index=>$index-2}) if $index > 1;

$str.=containHtml("snippet container", $prevbox) if $prevbox;

$str.="</td><td width=40% valign=top>";
$str.=htmlcode('displaywriteup', $index+1);
$str.="</td><td width=40% valign=top>";
$str.=htmlcode('displaywriteup', $index+2);

$str.="</td><td width=10% valign=top align=right>";

my $nextbox="";
foreach (@nextnodes){
  getRef $_;
  my $WRTYPE=getNode $$_{wrtype_writeuptype};
  $nextbox.="(".linkNode($_, $$WRTYPE{title})
    .")&nbsp;by&nbsp"
    .linkNode($$_{author_user})."<br>";
}
$nextbox = "<font size=2>$nextbox</font>" if $nextbox;
$nextbox.="<p>".linkNode($NODE, "next", {displaytype=>'cylinder', 
  index=>$index+2}) if @group > $index+2;

$str.=containHtml('snippet container', $nextbox) if $nextbox;
#build the next nodes box

$str .="</td></tr></table>";
$str.=htmlcode('votefoot');

my ($NODE1, $NODE2) = @group[$index, $index+1];
getRef $NODE1, $NODE2;
$str.=htmlcode('addwriteup') if $$NODE1{author_user} == getId($USER) or $$NODE2{author_user}==getId($USER); 

my $flag = 0;
foreach my $N (@group) {
  getRef $N;
  if ($$N{author_user} == getId($USER)) {
    $flag =1;
  }
}


$str.=htmlcode('softlink');
$str.=htmlcode('addwriteup') if not $flag and $index+2 > @group;
$str;
%]
