<table cellspacing="10" cellpadding="0" border="0" width="100%">

[%
  my @group = @{ $$NODE{group} } if $$NODE{group};

  my $numwriteups = int(@group);
  return unless $numwriteups;
  my $str;

  # $barbgcolor: background color of writeup separator bars
  my $barbgcolor = $$VARS{ekw_logobackground} || $$THEME{logoBackground} || "#305880";  

  my $count = 1;
  while($count < $numwriteups+1) {
    $str.="<tr>\n\t<td valign=\"top\">\n\n"
      .htmlcode('displaywriteuptitle', "$count, timestamp")
      ."</td></tr><tr><td valign=\"top\" class=\"writeup_text\">"
      .htmlcode('displaywriteuptext', $count)
      ."\n\t</td>\n</tr>"


	#next 3 lines added Saturday, April 5, 2008
	. '<tr><td>'
	. htmlcode('displayWriteupFooter', $count)
	. '</td></tr>'

;

   $count++;

    $str.='<tr><td bgcolor="' . $barbgcolor . '"><img src="http://static.everything2.com/dot.gif" height="2" width="1" alt="" /></td></tr>';

  }

  $str;

%]

</table>
