[%
  my @group = @{ $$NODE{group} } if $$NODE{group};

  my $numwriteups = int(@group);
  return unless $numwriteups;
  my $str;


  my $count = 1;
  while($count < $numwriteups+1) {
      $str .= '<p style="padding: 0 10px"> ' . htmlcode('displaywriteuptitle', "$count, timestamp")
      ."</p><table width=\"100%\"><tr><td class=\"writeup_text\" style='padding: 10px'>"
      .htmlcode('displaywriteuptext', $count)


	#next 2 lines added Saturday, April 5, 2008
	. '</td></tr><tr><td>'
	. htmlcode('displayWriteupFooter', $count)


      ."\n\t</td></tr></table>";
   $count++;

    $str.='<hr style="color: black; background-color: black; height: 1px; border: 0">';

  }
  $str;

%]
