node.page:
[%
$query->start_multipart_form("POST", $ENV{script_name}) .
$query->hidden("displaytype") .
$query->hidden("node_id", getId $NODE);

%]
<p align=right>[%linkNode $NODE, "display", {displaytype=>"display"};%]

<p>
[%
my $V = getVars $NODE;
my $str;

my $countkeys = int keys %$V;
foreach ($query->param) {
  if (/^remtrack_(\d+)$/) {
    delete $$V{"musictrack_$1"} if exists $$V{"musictrack_$1"};
  }

  if (/^title_(\d+)$/) {
     my $num = $1;
     my $title = $query->param($_);
     my $name = "file_$1";
     my $tmpfile = "/tmp/everythingmp3".rand(100000);
     my $dir = "/var/www/music";
     my $url = "music/";

     if ($query->param($name)) {
       my ($fname) = $query->param($name);
       my $musicname = $fname;



       $musicname =~ tr/ /_/;
       my @dos = split /\\/, $musicname;
       my @unix = split /\//, $musicname;

       if (@dos > @unix) {
         @unix = @dos;
       }
       $musicname = pop @unix;

       $str.= "'". $query->uploadInfo($fname)->{"Content-Type"}."'!";

       open OUTFILE, ">$tmpfile";
       my ($buf, $size, $bytes);
       while ($bytes = read($fname, $buf, 1024)) {
         $size += $bytes;
         print OUTFILE $buf;
       }
       close OUTFILE;


       `mv $tmpfile $dir/$musicname`;
       $$V{"musictrack_$num"} = "$title"."###".$url.$musicname;
       $str.= "$size bytes recieved for file $musicname<br>";
     }
  }


}
setVars ($NODE, $V);
$str;
%]

[%
my $magicnumber = 20;
my $start = 0;

my $V = getVars $NODE;
my @tracks;




foreach (keys %$V) {
  push @tracks, $_ if /^musictrack_\d+$/;
}

my $str;
$str.="<p>
BEWARE!  I haven't tested this too much yet, but it seems to work fine for me on Win and Linux.  Upload the files as you would an image.  Please name the files appropriately (uniquely) as a common name ('song.mp3') will overwrite each other.  It will probably take a long time to u/l, but wait it out -- don't stop it if it seems like it's hanging.



<br>";
foreach (sort @tracks) { 
  /musictrack_(\d+)/;
  my $num = $1;
  my ($track, $file) = split "###", $$V{$_};
  $str.= $query->checkbox("remtrack_$num", "", 1, "remove") . "$track -> $file<br>";
  $start = $num;
}

my $end = $start + $magicnumber;
foreach ($start+1..$end) {
  $_ = "0".$_ if length($_)== 1;
  my $track = $_;
  my $trackname = "musictrack_$_";

  $str.= $query->textfield("title_$track", ""). $query->filefield("file_$track")."<br>";
}


$str;


%]

[{closeform}]
