node.page:
[%
   my $str;

   my $startat = $query->param("startat");
   $startat =~ s/[^\d]//g;
   $startat ||= 0;

   my $pass = $DB->sqlSelect("count(*)", "nodetestresult", "nodetestresult_test=$$NODE{node_id} and nodetestresult_pass=1");

   my $fail = $DB->sqlSelect("count(*)", "nodetestresult", "nodetestresult_test=$$NODE{node_id} and nodetestresult_pass=0");

   $str.="<p align=\"center\"><i>".parseLinks($$NODE{nodetest_description})."</i></p>";
   $str.="<p align=\"right\">back to ".linkNode(getNode("Robotic Editor Shack", "oppressor_superdoc"))."</p>";
   $str.="Pass: $pass<br>";
$str.="FAIL: $fail<br>";


   my $csr = $DB->sqlSelectMany("nodetestresult_node", "nodetestresult", "nodetestresult_test=$$NODE{node_id} and nodetestresult_pass=0 limit $startat,100");

  $str.="<ul>Failures:";

  while(my $row = $csr->fetchrow_hashref)
  {
     my $n = getNodeById($$row{nodetestresult_node});
     unless($n){$DB->sqlDelete("nodetestresult", "nodetestresult_node=$$row{nodetestresult_node}"); next;}
     $str.="<li>".linkNode($n)."</li>";
  }

  $str.="</ul>";

  my @links;

  if($startat != 0)
  {
    my $highend = $startat;
    my $lowend = 0;
    $lowend = $startat - 100 if $startat > 100;
    $highend = $lowend + 100 if $lowend+100 >= $fail;
    push @links, linkNode($NODE, "$lowend-$highend", {'startat' => $lowend});
  }

  push @links,"$startat-".(($startat+100 <= $fail)?($startat+100):($fail));

  if($startat+100 <= $fail)
  {
    my $upperlow = $startat+100;
    my $upperhigh = $startat+200;
    $upperhigh = $fail if($upperhigh > $fail);
    push @links, linkNode($NODE, "$upperlow-$upperhigh", {'startat' => $upperlow});

  }

  $str.= "<p align=\"center\">".join(" | ", @links)."</p>";
  return $str;

%]
