node.page:
[% if ($query->param("op") eq "new") {
	$$NODE{parent_node} = $query->param("lastnode_id");
	updateNode $NODE, $USER;

}
  if ($$NODE{parent_node}) {
	my $PARENT = selectNode ($$NODE{parent_node});
	return "<font size=2><p align=right>in reply to " . linkNode ($PARENT) ."</font><br><p>";
}
%]

