node.page:
[%


my $TAGNODE = getNode 'approved html tags', 'setting';
my $TAGS=getVars($TAGNODE);

my $text = $APP->htmlScreen($$NODE{description}, $TAGS);
$text = parseLinks($text);

my $str;
$str.= <<ENDTEXT;

<object type="application/x-shockwave-flash" data="http://static.everything2.com/player_mp3_maxi.swf" width="300" height="20">
    <param name="movie" value="http://static.everything2.com/player_mp3_maxi.swf" />
    <param name="bgcolor" value="#ffffff" />
    <param name="FlashVars" value="mp3=$$NODE{link}&amp;width=300&amp;autoload=0&amp;volume=50&amp;showstop=1&amp;showinfo=1&amp;showvolume=1" />
</object>
<h2><a href='$$NODE{link}'>download mp3</a></h2>
ENDTEXT

$str.="$text";
$str.='<p align="right">('.linkNode($NODE, 'edit', {'displaytype'=>'edit', 'lastnode_id'=>0}).")</p>" if canUpdateNode($USER, $NODE);

return $str;


%]
