node.page:
[%

return "You can't edit this, don't be ridiculous." unless(canUpdateNode($USER, $NODE));


# This code does the update, if we have one.
my $param;
my @params = $query->param;

foreach $param (@params)
{
  if ($param =~ /^update_(\w*)$/)
  {
    $$NODE{$1} = $query->param($param);
  }
}

updateNode($NODE, $USER);



my $str = my $str='<p align="right">('.linkNode($NODE, 'display', {'displaytype'=>'display', 'lastnode_id'=>0}).")</p>";

$str.= htmlcode("openform");
$str.= buildTable(['key','value'],[
    {'key'=>'Title','value'=>
    $query->textfield( -name => "update_title", -default => $$NODE{title}, -size => 40, -maxlength => 255 )},
    {'key'=>'Introduction','value'=>
    $query->textarea( -name => "update_doctext", -default => $$NODE{doctext}, -rows => 7, -columns => 50 )},

  ],'nolabels','center');

$str.=htmlcode("closeform");
return $str;
%]
