node.page:
[%
my $str = '<p><cite>by&nbsp;' . linkNode ($$NODE{author_user}) . '</cite></p>';
$str .= '<p>'.linkNode($NODE, "View this stylesheet", {displaytype => 'view'}).'</p>';

return $str if $APP->isGuest($USER) ;
if ($query->param('userstyle')) {
  $$VARS{userstyle} = $$NODE{node_id};
}

if ($$USER{user_id} == $$NODE{author_user} or $APP->isAdmin($USER) ) {
	my $VARSNODE = getNode( 'fixed stylesheets' ,'setting' ) ;
	my $settings = getVars( $VARSNODE ) ;

  if ( $query -> param( 'fixstylesheet' ) ) {
  	my $saveold = ( $query -> param( 'saveold' ) ? '1' : '0' ) ;
  	$str .= '<p><strong>The stylesheet has been fixed. Changes will only become permanent
		when you click on "stumbit".</strong></p>
		<textarea cols="60" rows="30" name="stylesheet_doctext">'.
		$APP->fixStylesheet($NODE,$saveold ).'</textarea>' ;
  }
  else {
	# update fixed setting if doesn't need fixing:
  	$APP->fixStylesheet($NODE, 0) unless $$settings{ $$NODE{ node_id } } >= $Everything::CONF->{stylesheet_fix_level} ;
	$str .= "<p><strong>Edit the stylesheet:</strong></p>".
		htmlcode("textarea","doctext,30,60");
  }

	if ( $$settings{ $$NODE{ node_id } } < $Everything::CONF->{stylesheet_fix_level} and
			!$query -> param( 'fixstylesheet') ){
		$str .= '<p>You can automatically fix this stylesheet to adapt it to many of the recent changes in the E2 code.
		Changes will not become permanent until you stumbit again.</p>
		<p><input type="submit" name="fixstylesheet" value="Autofix this stylesheet">
		<label><input type="checkbox" name="saveold" value="1"> Save old selectors in comments</label>
		</p>';
	} else {
                my $inmenu = $APP->getParameter($NODE, "supported_sheet"); $inmenu ||= 0;

		if ($query -> param('sexisgood') && $query->param('inmenu')){
			$APP->setParameter($NODE, -1,'supported_sheet',1);
                        $inmenu = 1;
		}elsif ($query->param('sexisgood') && !$query->param('inmenu')){
                        $APP->delParameter($NODE, -1,'supported_sheet');
                        $inmenu = 0;
                }
		$str .= $query -> checkbox(-name => 'inmenu', value => 1 ,
			checked => $inmenu, label => 'Include in theme menu' );
	}

  if ($$USER{user_id} == $$NODE{author_user}) {
    $str .="<p>Talk to your users:</p>";
  }
  else {
    $str .="<p>Talk to the stylesheet's users:</p>";
  }

  $str .= '<input type="text" name="style_msg" size="50" value="">';
  $str .= "<br>";

  my $msg = $query -> param('style_msg');

  #Trim whitespace so that we don't send blank /msgs.
  $msg =~ s/^\s*//;
  $msg =~ s/\s*$//;

  # Send /msg to users if there is a /msg to send --[Swap]
  if ($msg) {
    my $csr = $DB -> sqlSelectMany('setting_id',
                                   'setting',
                                   "vars like '%userstyle=$$NODE{node_id}%'");
    my $numusers = $csr -> rows;
    my $maxusers = 500;

    if ($numusers > $maxusers) {
      # Kernel blue has about 15,000 users at the time of this coding
      # (April 2009). Somehow, I don't think that /msging 15,000
      # people at once is a good idea. --[Swap]
      $str .= "<p><small>Sorry, you have too many users! Talk to [e2
      staff|an admin or editor] to make a general announcement on the
      front page instead if you really need to.</small></p>";
    }
    else {
      my @stylesheet_users;
      while (my $row = $csr -> fetchrow_hashref) {
        my $uid = $$row{'setting_id'};
        my $user = getNodeById($uid) -> {'title'};
        push @stylesheet_users,$user;
      }
      htmlcode('sendPrivateMessage',{
                             'recipient' => \@stylesheet_users,
                             'message' => $msg,
                             });

      #No XSS!
      $msg =~ s/\</\&lt\;/g;
      $msg =~ s/\>/\&gt\;/g;

      $str .= "<p><small>You said, <i>\"$msg\"</i> (sent to "
        .@stylesheet_users." users)</small></p>\n";
    }
  }
}

my %autofix = ( autofix => '1' ) if $$USER{ user_id } == $$NODE{ author_user } ;
$str .= '<p>'
	.linkNode( $NODE , 'Try this stylesheet out' , {
		displaytype => 'choosetheme'
		, theme => $$NODE{ node_id }
		, noscript => 1
		, -id => 'testdrive'
		, %autofix
	})
	.'</p>' ;

$str .= "<p><input type='checkbox' ".
  ( $$VARS{userstyle} == $$NODE{node_id} ?"checked='checked''" :"").
  " name='userstyle' value='".$$NODE{node_id}.
  "'> Use this stylesheet</p>";

$str .= '<input type="button" value="Preview this style"
                  id="previewstyle">';

$str;

%]
<script type="text/javascript">
        var zenSheet = document.getElementById('zensheet');
        var	newStyle = document.createElement('link');
        newStyle.setAttribute('rel', 'stylesheet');
        newStyle.setAttribute('type', 'text/css');
        newStyle.setAttribute('href', '/index.pl?node_id=[% $NODE->{node_id} %]&amp;displaytype=serve');
        document.getElementsByTagName('head')[0].appendChild(newStyle);

        newStyle.disabled = true; // belt & braces for old IE
        zenSheet.disabled = false ; // ditto
        var newValue = 'Revert' ;
        document.getElementById('previewstyle').onclick = function () {
	var oldValue = this.getAttribute('value');
	this.setAttribute('value', newValue);
	newValue = oldValue;
	zenSheet.disabled = ( ! zenSheet.disabled ) ;
	newStyle.disabled = ( ! newStyle.disabled ) ;
        }
</script>
