node.page:
[{votehead}]
[{show writeups}]

<div id='displaytypelinks'>
<p>[% return if $APP->isGuest($USER); linkNode($NODE, 'link view', { displaytype => 'linkview' } ) %] </p>
</div>

[{votefoot}]

[%
return '' unless $HTMLVARS{isDevServer};
return '' unless $$NODE{type}{title} eq 'writeup' and $$NODE{parent_e2node};
my $str;
# Stars for the parent e2node:
my $PARENT = getNodeById($$NODE{parent_e2node});
return unless($PARENT and $$PARENT{group} and my @grp = @{getNodeById($$NODE{parent_e2node})->{group}});
my $nwriteups = scalar(@grp);
if ($nwriteups==1) {
  $str.=htmlcode('stars',"$$NODE{parent_e2node},,,,");
  $str.=htmlcode('nagbox');
}
else {
  $str.=htmlcode('stars',"$$NODE{parent_e2node},1,,,")
  .'<p>This writeup is part of a larger node - please view '.linkNode($PARENT).' to rate it.</p>';  
}
return $str;
%]

[{writeuphints}]

[%
return unless $$NODE{parent_e2node};
'<div id="softlinks">
'.htmlcode('softlink').'
</div>';
%]

[%
return unless $$NODE{type}{title} ne 'draft' && canUpdateNode($USER, $NODE) or
	$$NODE{type}{title} eq 'draft' && htmlcode('canseedraft', $NODE, 'edit') == 1;

htmlcode('openform', -class=>'writeup_add')
	.htmlcode('editwriteup', $NODE)
	.'</form>';
%]
