node.nlcode:
[%

my $str;

my $csr = $DB->sqlSelectMany("(select title from node where node_id=cooledby_user limit 1) as cooluser, coolwriteups_id, (select title from node where node_id=(select author_user from node where node_id=coolwriteups_id limit 1) limit 1) as wu_author, (select parent_e2node from writeup where writeup_id=coolwriteups_id limit 1) as parentNode, (select title from node where node_id=parentNode limit 1) as parentTitle", "coolwriteups", "", "order by tstamp desc limit 100");

#my $csr = $DB->sqlSelectMany("(select title from node where node_id=cooledby_user limit 1) as cooluser, coolwriteups_id, (select title from node where node_id=(select author_user from node where node_id=coolwriteups_id limit 1) limit 1) as wu_author, (select parent_e2node from writeup where writeup_id=coolwriteups_id limit 1) as parentNode, (select title from node where node_id=parentNode limit 1) as parentTitle, (select cooled from writeup where writeup_id=coolwriteups_id limit 1) as writeupCooled", "coolwriteups", "", "order by tstamp desc limit 50");

my $xml;
use XML::Generator;
my $XG = new XML::Generator;

my $count = 15;
my %used;

$str.="\n\t<ul class=\"linklist\">";

while (my $CW = $csr->fetchrow_hashref()) {
  next if exists $used{$$CW{coolwriteups_id}};
  $used{$$CW{coolwriteups_id}} = 1;
  $str.="\n\t\t<li>".linkNode($$CW{coolwriteups_id}, $$CW{parentTitle}, {lastnode_id => 0}) ."</li>";
#  $str.="<li>".linkNode($$CW{coolwriteups_id}, $$CW{parentTitle}, {lastnode_id => 0}) ." (".$$CW{writeupCooled}.")</li>";
  $xml .="\t".$XG->cooled({node_id => $$CW{coolwriteups_id}, 
     parent_e2node => $$CW{parentNode}, 
     author_user => $$CW{wu_author},
     cooledby_user => $$CW{cooluser}},
     $$CW{parentTitle})."\n";
  last unless (--$count);
}

$str.= parseLinks( "\n\t</ul>
	<div class='morelink cup_more'>([Cool Archive|more])</div>" ) ;

$csr->finish;

my $SETTINGS = getVars(getNode('system settings','setting'));
$xml = $XG->COOLEDNODES(
   $XG->INFO({site => $$SETTINGS{site_url}, sitename => $$SETTINGS{site_name},  servertime => scalar(localtime(time))}, 'Rendered by the Cool Nodes XML Ticker')
   . "\n".$xml . "\n");

my $XNODE = getNode('cool nodes XML ticker', 'fullpage');
$$XNODE{doctext} = $xml;
updateNode($XNODE, -1);

$str;
%]
