node.nlcode:
[%
my $repthreshholdlo = 12;
my $repthreshholdhi = 20;
my $limit = 4;
my $length = 512;

my $csr = $DB->sqlSelectMany(
	'wu.node_id, wu.author_user,
		parent_e2node,
		doctext,
		type.title as type_title'
	,
	'node wu
		JOIN writeup ON writeup_id= wu.node_id 
		JOIN document on document_id = wu.node_id
		JOIN node type on type.node_id = writeup.wrtype_writeuptype'
	,
	"wu.reputation > $repthreshholdlo
		AND wu.reputation < $repthreshholdhi
		AND writeup.cooled != 0"
	,
	"ORDER BY writeup.publishtime DESC LIMIT $limit"
);

my $str = htmlcode( 'show content' , $csr , 'parenttitle, type, byline,'.$length ) ;

# stop PRE breaking layout: replace PRE with TT; fix spaces and newlines to still work
while ( $str =~ /<pre>(.*?)<\/pre>/si )
	{
	my $temp = $1;
	$temp =~ s/\n/<br>/g;
	$str =~ s/<pre>(.*?)<\/pre>/<tt>$temp<\/tt>/si;
	$str =~ s/  / &nbsp;/g; # replace two spaces with space and nbsp
	}
$str =~ s!<hr.+>!<hr>!ig ; # no width on hr to avoid stretch

"<div id=\"cotc\">\n$str\n</div>" ;
%]
