node.nlcode:
[%
return "" unless isGod($USER);
my $str;

my $PARAMS = { op => 'addBucket', "bnode_" . $$NODE{node_id} => 1 };
$str .= linkNode($NODE, "Add '$$NODE{title}'", $PARAMS);


my @bnodes;
@bnodes = split ',', $$VARS{nodebucket} if (defined($$VARS{nodebucket}));
my $isGroup = 0;
$isGroup = 1 if $$NODE{type}{grouptable};

return $str."<p>Empty<br>\n" unless(@bnodes);

$str.= htmlcode('openform');
$str.=$query->hidden(-name => "op", -default => "bookop", -override => 1);

foreach my $node (@bnodes)
{
  getRef($node);

  # Can't use CGI::checkbox here because it insists on having a label...
  $str .= "<INPUT TYPE='checkbox' NAME='bnode_$$node{node_id}' " .
     "VALUE='ON' UNCHECKED>";
  $str .= linkNode($node) . "<br>\n";
}

$str .= "<font size=1>\n";

$str .= "<INPUT TYPE='checkbox' NAME='dropexec' VALUE='ON' CHECKED>" .
  "Execute and drop<br>\n" if($isGroup);

if($isGroup)
{
  $str .= $query->submit( -name => "bgroupadd", -value => "Add to Group") ."\n";
}

$str .= $query->submit( -name => "bdrop", -value => "Drop" ) . "\n";

$str .= "</font>\n";

$str."</form>";
%]
