node.nlcode:
[%
if((exists $VARS->{lockCustomHTML})) { 	#may later say why locked? or give a msg field to anon admin?
	return '<p>Sorry, the Outside URLs nodelet is currently locked, probably because an admimistrator is working with your account. It should soon be back to normal.</p>';
}

#note: this assumes all the URLs and display text have already been properly screened

my $numAllowed = $APP->getLevel($USER) || 0;
return '<p>You need to be a higher level to use the Outside URLs nodelet.</p>' if $numAllowed<1;
$numAllowed += 2;
$numAllowed = 20 if $numAllowed>20;

my $str='';

my $link;
my $linkDisp;
my $i=0;
for(;$i<$numAllowed;++$i) {
	last unless exists $VARS->{'urlLink'.$i};
	$link = $VARS->{'urlLink'.$i};
	$linkDisp = (exists $VARS->{'urlDisp'.$i}) ? $VARS->{'urlDisp'.$i} : $link;

	$str .= '<li><a href="'.$link.'" target="_blank">' . $linkDisp . "</a></li>\n";
}

return '<p><em>You currently have no outside links set.</em></p>' if $i==0;

return "<ul>\n" . $str . '</ul>';
%]

<p align="right"><small><small>(<a href=[% urlGen({'node'=>'Outside URLs nodelet editor','type'=>'superdoc'}); %]>edit</a>)</small></small></p>
