node.nlcode:
[%
my $repthreshholdlo = 18;
my $repthreshholdhi = 26;
my $limit = 4;
my $length = 280;

my $csr = $dbh->prepare("SELECT writeup_id FROM writeup LEFT JOIN node ON node_id=writeup_id WHERE reputation > $repthreshholdlo AND reputation < $repthreshholdhi AND cooled != 0 ORDER BY writeup_id DESC LIMIT $limit");

#my $csr = $dbh->prepare("SELECT writeup_id FROM writeup LEFT JOIN node ON #node_id=writeup_id WHERE reputation > $repthreshholdlo AND reputation < #$repthreshholdhi AND cooled != 0 ORDER BY writeup_id LIMIT $limit");
#4-1-02 only -- nate

$csr->execute();
my $str;

my $HTML = getVars(getNode('approved HTML tags', 'setting'));
$$HTML{hr} = ''; # dissallow width for HR so it doesn't stretch

$str.='<table cellpadding="0" cellspacing="0">';

my %used;
while (my ($WRITEUP) = $csr->fetchrow) {
	getRef $WRITEUP;
        #next unless $WRITEUP;
        #if for any reason we get these reports, we'll need to uncomment the 
        # top line
        return "BAD WRITEUP REF. TELL [JAYBONCI]." unless($WRITEUP);

	my $text;
	if (length($$WRITEUP{doctext}) < $length) {
		$text=parseLinks(htmlScreen($$WRITEUP{doctext}, $HTML));
	} else {
		my $firstwords = substr($$WRITEUP{doctext},0, 512);
		$firstwords =~ s/\s+\w*$//gs;
		$firstwords = parseLinks(htmlScreen($firstwords, $HTML));
		$firstwords =~ s/\[.*?$//;
		# if the text has an opening table tag but no closing, add one.
		# note: this prototype fails for multiple table tags.
		if ($firstwords =~ m/<table/) {
			unless ($firstwords =~ m/<\/table/) {
				$firstwords .= '</table>'; 
			}
		}
		$text=$firstwords .'... <div align="right"><strong>('
		.linkNode($WRITEUP, 'more',{lastnode_id =>0}).')</strong></div><BR />';
	}

        $str .= "<tr bgcolor=\"\#ffffff\"><td class=\"oddrow\"><span class=\"cotc_title\">"
	.linkNode($$WRITEUP{parent_e2node},'',{lastnode_id =>0}). '&nbsp;'.linkNode($WRITEUP, '('.getNodeById($$WRITEUP{wrtype_writeuptype})->{title}
.')',{lastnode_id =>0}
	). '</span></td><td class="oddrow" align="right"><span class="cotc_author"> by '. linkNode($$WRITEUP{author_user},'',{lastnode_id =>0}).'</span></td></tr>';
	$str.="<tr><td colspan=\"2\">$text</td></tr>";
	$str.='<tr><td bgcolor="#000000" colspan="2">'.htmlcode('clearimage','2,1')."</td></tr>\n";
}
$csr->finish;
$str.='</table>';
$str;
%]
