node.nlcode:
[%
# Start with C!s from [rtnsection_cwu]
my $str='';
$str.='<h4>'.linkNode(getNode('An Introduction to Everything2','e2node'), 'About Everything2').'</h4>';
$str.='<h4>'.linkNode(getNode('Cool Archive','superdoc'), 'User Picks').'</h4>';
$str.='<ul class="infolist">';
my $csr = $DB->sqlSelectMany("distinct coolwriteups_id", "coolwriteups", "", "order by tstamp desc limit 6");

while(my $row = $csr->fetchrow_hashref()){
	my $cw = getNodeById($$row{coolwriteups_id});
	next unless($cw);
	my $np = getNodeById($$cw{parent_e2node});
	$str .= '<li>'.linkNode($cw, $$np{title}, {lastnode_id => 0});
}

$csr->finish();
$str.='</ul>';

# From [rtnsection_edc]
$str.= '<h4>'.linkNode(getNode('Page of Cool','superdoc'), 'Editor Picks').'</h4>';

$str.= '<ul class="infolist">';
my $poclink = getId(getNode('coollink', 'linktype'));
my $pocgrp = getNode('coolnodes', 'nodegroup')->{group};
my $count = 0;

foreach(reverse @$pocgrp)
{
	last if($count >= 4);
	$count++;

	next unless($_);

	my $csr = $dbh->prepare('SELECT * FROM links WHERE from_node=\''.getId($_).'\' and linktype=\''.$poclink.'\' limit 1');

	$csr->execute;

	my $coolref = $csr->fetchrow_hashref;

	next unless($coolref);
	$coolref = getNodeById($$coolref{from_node});
	next unless($coolref); 
	$str .= '<li>'.linkNode($coolref,$$coolref{title}, {lastnode_id => 0}).'</li>';

	$csr->finish();

}

$str.='</ul>';


return $str;

%]
