node.nlcode:
[%

my @NODELIST;
my $str;
my $n;
my $limit = 15;

my $csr = $DB->sqlSelectMany( 'from_node'
	, 'links JOIN node ON node.node_id = links.from_node'
	, "linktype = ".getId(getNode('coollink','linktype'))
	. " AND node.type_nodetype = " . getId(getNode('e2node','nodetype'))
	. " ORDER BY rand() LIMIT $limit"
	);

while ($n = $csr->fetchrow_hashref) {
push @NODELIST, linkNode($n->{from_node}, '', {lastnode_id => 0});
}

$str = "
         <ul class=\"linklist\">";

$str .= "\n\t\t<li>" . join("</li> \n\t\t<li>",@NODELIST) . "</li>";

$str .= parseLinks( '
        </ul>
        <div class="nodeletfoot morelink">([Page of Cool|more])</div>
' ) ;

return $str;

%]
