node.nlcode:
[% 
return "";
#nate 2009-7-11

my $YESTERDAYSTATS = $DB->sqlSelectHashref('*', 'stats', 'TO_DAYS(NOW()) - TO_DAYS(stattime) > 1', 'order by stattime desc LIMIT 1');
my $TODAYSTATS;

$TODAYSTATS->{nume2nodes} = $DB->sqlSelect("count(*)", "e2node");
$TODAYSTATS->{numwriteups} = $DB->sqlSelect("count(*)", "writeup");
$TODAYSTATS->{numusers} = $DB->sqlSelect("count(*)", "user");
$TODAYSTATS->{numlinks} = $DB->sqlSelect("count(*)", "links");
$TODAYSTATS->{xpsum} = $DB->sqlSelect("sum(experience)", "user");
$TODAYSTATS->{nodehits} = $DB->sqlSelect("sum(hits)", "node");
$TODAYSTATS->{numcools} = $DB->sqlSelect('count(*)', 'coolwriteups'); 
$TODAYSTATS->{numedcools} = $DB->sqlSelect('count(*)','links','linktype='.getId(getNode("coollink", "linktype")));
$TODAYSTATS->{numvotes} = $DB->sqlSelect("count(*)", "vote");

$DB->sqlInsert("stats", $TODAYSTATS);

#stats DB table is not getting updated, so N-Wing added SinceJune23 to
#things until the DB table is fixed again (June 23 was the last update)

my $str = '';

my $nodes = $DB -> sqlSelect('count(*)', 'e2node');
my $firmlinks = $DB->sqlSelect('count(*)', 'links', 'linktype='.getId(getNode('firmlink', 'linktype')));
my $usersonline = $DB->sqlSelect('count(*)', 'room','visible=0');

$str.="nodes: $nodes\n";

my $writeups = $DB->sqlSelect('count(*)', 'writeup');
$str.="writeups: $writeups\n";
$str.='writeupstoday: '.($$TODAYSTATS{numwriteups} - $$YESTERDAYSTATS{numwriteups})."\n";

$str.="links: $$TODAYSTATS{numlinks}\n";
$str.="firmlinks: $firmlinks\n";


$str.="users: $$TODAYSTATS{numusers}\n";
$str.='userstoday: '.($$TODAYSTATS{numusers}-$$YESTERDAYSTATS{numusers})."\n";

$str.="votes: $$TODAYSTATS{numvotes}\n";
$str.='votestoday: '.($$TODAYSTATS{numvotes}-$$YESTERDAYSTATS{numvotes})."\n";

$str.="cools: $$TODAYSTATS{numcools}\n";
$str.='coolstoday: '.($$TODAYSTATS{numcools}-$$YESTERDAYSTATS{numcools})."\n";

$str.="hits: $$TODAYSTATS{nodehits}\n";
$str.='hitstoday: '.($$TODAYSTATS{nodehits}-$$YESTERDAYSTATS{nodehits})."\n";

$str.="usersonline: $usersonline";

$str;
%]
