node.nlcode:
[%

use Time::HiRes;
my @start = Time::HiRes::gettimeofday;
my $timeStr;
my $timeCount = 1;


my $str;

my $csr = $DB->sqlSelectMany("(select title from node where node_id=cooledby_user limit 1) as cooluser, coolwriteups_id, (select title from node where node_id=(select author_user from node where node_id=coolwriteups_id limit 1) limit 1) as wu_author, (select parent_e2node from writeup where writeup_id=coolwriteups_id limit 1) as parentNode, (select title from node where node_id=parentNode limit 1) as parentTitle, (select cooled from writeup where writeup_id=coolwriteups_id limit 1) as writeupCooled", "coolwriteups", "", "order by tstamp desc limit 30");

my $xml;
use XML::Generator;
my $XG = new XML::Generator;

my $count;

$count = 15;
my %used;
my @cools;
while (my $CW = $csr->fetchrow_hashref()) {
  next if exists $used{$$CW{coolwriteups_id}};
  $used{$$CW{coolwriteups_id}} = 1;
  my $row;
#  $row.="<tr><td align='center'>".linkNode($$CW{coolwriteups_id}, $$CW{parentTitle}, {lastnode_id => 0}) ."</td></tr>";
  $row.="<tr><td align='center'>".linkNode($$CW{coolwriteups_id}, $$CW{parentTitle}, {lastnode_id => 0}) ." (".$$CW{writeupCooled}.")</td></tr>";
  $row.="<tr><td bgcolor='#000000'>".htmlcode('clearimage','0,1')."</td></tr>";
  $str .= $row;
  $xml .="\t".$XG->cooled({node_id => $$CW{coolwriteups_id}, 
     parent_e2node => $$CW{parentNode}, 
     author_user => $$CW{wu_author},
     cooledby_user => $$CW{cooluser}},
     $$CW{parentTitle})."\n";
  last unless (--$count);
}

$csr->finish;

$xml = $XG->COOLEDNODES(
   $XG->INFO({site => $Everything::CONF->{system}->{site_url}, sitename => $Everything::CONF->{system}->{site_name},  servertime => scalar(localtime(time))}, 'Rendered by the Cool Nodes XML Ticker')
   . "\n".$xml . "\n");

my $XNODE = getNode('cool nodes XML ticker', 'fullpage');
$$XNODE{doctext} = $xml;
updateNode($XNODE, -1);

$str;
%]
