node.nodetest_code:
my ($E2NODE) = @_;

my @grp = getNodeWhere({ 'title' => $$E2NODE{title}, 'type_nodetype' => $$E2NODE{type_nodetype} 
});
return unless(@grp);
return "y" unless @grp > 1;
return "n";

node.nlcode:
my ($E2NODE) = @_;

my @grp = getNodeWhere({ 'title' => $$E2NODE{title}, 'type_nodetype' => $$E2NODE{type_nodetype} 
});
return unless(@grp);
return "y" unless @grp > 1;
return "n";
