node.nodetest_code:
my ($WRITEUP) = @_;

my $webby = getNode("Webster 1913", "user");
my $def = getNode("definition", "writeuptype");
return "y" unless($$WRITEUP{author_user} == $$webby{node_id});
return "n" unless($$WRITEUP{wrtype_writeuptype} == $$def{node_id});
return "y";

