node.nodetest_code:
my ($NODE) = @_;

if(not exists($$NODE{title}) or $$NODE{title} eq "" or "$$NODE{title}" eq "0" or "$$NODE{title}" eq " ")
{
  $$NODE{title} = "Damaged node - $$NODE{type}{title} - id: $$NODE{node_id}. Please report this error. (nodetest sweep)";
  updateNode($NODE, -1);
  return "f";
}
  return "y";

node.nlcode:
my ($NODE) = @_;

if(not exists($$NODE{title}) or $$NODE{title} eq "" or "$$NODE{title}" eq "0" or "$$NODE{title}" eq " ")
{
  $$NODE{title} = "Damaged node - $$NODE{type}{title} - id: $$NODE{node_id}. Please report this error. (nodetest sweep)";
  updateNode($NODE, -1);
  return "f";
}
  return "y";
