node.code:
my $author_link = getNodeById($$args{author_id}) ?
  linkNode($$args{author_id})
  : "(who has been unpersoned) "
  ;
my $target_wu = getNodeById($$args{writeup_id});
my $wu_link =  $target_wu ?
  "the writeup titled " . linkNode($$args{writeup_id})
  : " a writeup which has since been removed"
  ;

return "$author_link <strong>blanked</strong> $wu_link.";

node.invalid_check:
my $target_wu = getNodeById($$args{writeup_id});
my $trimmedNewText = $$target_wu{'doctext'} if $target_wu;
$trimmedNewText =~ s/^\s+|\s$//;
my $isBlank = ($trimmedNewText eq '' || length $trimmedNewText < 20);
my $isDraft = $$target_wu{type}{title} eq 'draft' if $target_wu;
my $isInvalid = !$isBlank || $isDraft || !$target_wu;
return $isInvalid;
