node.invalid_check:
my $draft = getNodeById($$args{draft_id});
my $status = getNodeById($$draft{publication_status});
return ($$status{title} ne 'review');

node.code:
my $draft_id = $$args{draft_id} || 0;
my $nodenote_id = $$args{nodenote_id} || 0;
my $draft = getNodeById($draft_id);

my $suspender = $DB->sqlSelect(
	'nodelock_user'
	, 'nodelock'
	, "nodelock_node=$$draft{author_user}"
);

my $sustext = 'put on node forbiddance' if $suspender;

$suspender ||= $DB->sqlSelect(
	'suspendedby_user'
	, 'suspension'
	, "suspension_user=$$draft{author_user}
         AND suspension_sustype="
		 .getId(getNode('writeup', "sustype"))
);

if ($suspender){
	$sustext ||= 'suspended';
	my $user = $suspender == $$USER{user_id} ? 'you'
		: linkNode($suspender);
	$sustext = " (who $user $sustext)";
}

my $numReviews = $DB->sqlSelect(
	'COUNT(nodenote_id)'
	, 'nodenote'
	, "nodenote_nodeid = $draft_id
		AND nodenote_id > $nodenote_id"
);

my $numReviewText= "";
my $s = 's';
if ($numReviews == 1) {
	$numReviewText = "<br>1 note was left since request.";
} elsif ($numReviews > 1) {
	$numReviewText = "<br>$numReviews notes were left since request.";
} elsif ($$args{neglected}){
	my $days = $DB -> sqlSelect('DATEDIFF(NOW(), timestamp) as days'
		, 'nodenote', "nodenote_id=$nodenote_id");
	$numReviewText = " <strong><em>$days days</em> ago.</strong>";
	$s = 'ed'
}

return '<cite>'.linkNode($$draft{author_user})
	."</cite>$sustext request$s review of <b>"
	.linkNode($draft)
	.'</b>'
	.$numReviewText;
