node.code:
return unless isGod($USER);
my $U = $query->param('bestow_id');
getRef $U;

$$U{votesleft} += 25;
$$U{karma} += 1;

$APP->securityLog(getNode("bestow","opcode"), $USER, "$$U{title} was given 25 votes by $$USER{title}");

updateNode($U, -1);
$U = getNodeById(getId($U), 'force');
if (getId($U) == getId($USER)) { $USER = $U ; }
