node.code:
return unless(exists($HTMLVARS{'group_gods'}{$$USER{node_id}}));
return unless($query->param('fixwu'));

my $wu = getNodeById($query->param('fixwu'));
return unless($wu);
return unless($$wu{type_nodetype} == getId(getType('writeup')));

my $cools = $DB->sqlSelect("count(*)", "coolwriteups", "coolwriteups_id=$$wu{writeup_id}");

my $csr = $dbh->prepare("UPDATE writeup SET cooled=$cools WHERE writeup_id=$$wu{writeup_id}");

$csr->execute();
