node.code:
return unless (exists($HTMLVARS{'group_gods'}{$$USER{user_id}}) ) || (exists($HTMLVARS{'group_content editors'}{$$USER{user_id}}));
return unless htmlcode('verifyRequest', 'flag');

my $flagType = $query->param('flag');
my $flagID = $query->param('flag_id');

return unless getNodeById($flagID);
return unless $flagType;

my $fLink = getNode("flag","linktype");
$fLink = $$fLink{node_id};

my $flagNode = getNode($flagType,"flag");
$flagNode = $$flagNode{node_id};

return unless $flagNode;

my $flagExists = $DB->sqlSelect("count(*)","links","linktype = $fLink and from_node = $flagNode and to_node = $flagID");

if ($flagExists) {
$DB->sqlDelete("links","linktype = $fLink and from_node = $flagNode and to_node = $flagID");
}
else {
$DB->sqlInsert("links",{linktype => $fLink, from_node => $flagNode, to_node => $flagID});
}
