node.code:
return unless isGod($USER) || (exists $HTMLVARS{'group_e2contact'}{$$USER{node_id}}) || (exists $HTMLVARS{'group_chanops'}{$$USER{node_id}});
my $currentRoomId = int($$USER{in_room});
my $currentRoom = getNode($currentRoomId);
my $currentRoomName;
if (!$currentRoom && $currentRoomId) {
  $currentRoomName = "in expired room (#$currentRoomId)";
} elsif (!$currentRoom) {
  $currentRoomName = "outside";
} else {
  $currentRoomName = "in room '$$currentRoom{title}'";
}
my $flushNode = getNode('flushcbox', 'opcode');
$DB->sqlInsert('seclog', {
   'seclog_node' => $$flushNode{node_id},
   'seclog_user' => $$USER{user_id},
   'seclog_details' => "Chat $currentRoomName flushed."
});
$DB->sqlDelete("message", "for_user = 0 AND room = $currentRoomId");
return 1;
