node.code:
return unless (Everything::isApproved($USER, getNode('Content Editors', 'usergroup')) || isGod($USER));

my @params = $query->param;
my @deathrow;
my $nr = getId(getNode('node row', 'superdoc'));
foreach(@params) {
	next unless /^killnode(\d+)$/;
	next if $DB->sqlSelect('linkedby_user', 'weblog', "weblog_id=$nr and to_node=$1"); #hopefully, this will prevent double-kills

	push @deathrow, $1;
}

return unless @deathrow;

my $mass = getNode('massacre', 'opcode');
my $UID = $$USER{node_id}||0;	#ID of person that is deleting
return if !$UID;
my $aid; #ID of user whose WU is killed
#my $Kid = getId(getNode('Klaproth','user'));
my $V; #vars of that user
my $nid;
my $m;
my $r; #reason
my $nt; #node title
my $z;

foreach $nid (@deathrow) {

	my $N = getNodeById($nid);
	next unless($N);
	#security fix to make sure that we can't delete non-writeups.
	next unless($$N{type_nodetype} == getId(getType('writeup')));
	$aid = $$N{author_user};
         my $parentID = $$N{parent_e2node};
	my $amount = -5;
         my $noexp = 0;

         foreach (values %HTMLVARS) {
            if ($parentID eq $_) {
               $noexp = 1;
               last;
            }
         }

         unless($noexp)
         {
	   adjustExp($aid, $amount);
         }

	if(
		!$query->param('instakill'.$nid)
	) {
		$DB->sqlInsert('weblog',
		{weblog_id => $nr,
		to_node => $nid,
		linkedby_user => getId($USER),
		-linkedtime => 'now()'});
	} else {
		#jb says nukeNode($N, $USER) would never work
		#Because CE's are not valid writeup deleters in
		#the nodetype. Instead of changing the nodetype, change
		#this codepath here.
		nukeNode($N, -1);
	}

	if($query->param('hidewu'.$nid)) {
		$DB -> sqlUpdate('newwriteup', { notnew=>'1' }, "node_id=$nid");
		$DB -> sqlUpdate('writeup', { notnew=>'1' }, "writeup_id=$nid");
		$$N{notnew} = '1' ; # update cached version
   }

	next unless $aid;	#skip /msg if no WU author
	next if ($aid==$UID) && $query->param('noklapmsg'.$nid);	#no /msg to self

	#N-Wing thinks it may make more sense for the user to just block Klaproth, instead of a special VARS value for that:
	$V = getVars($aid);
	$z = $$V{'no_notify_kill'} ? 0 : 1;

	$nt = $$N{title};
	if($nt=~/^(.*) \(.+?\)$/) { $nt=$1; }
	if($z && $noexp) {
		#don't send msg for maintenance stuff, unless there is a reason
		$z=0 unless (defined $query->param('killreason'.$nid)) && (length($query->param('killreason'.$nid))!=0);
	}

	next unless $z;	#not msg-worthy

	$m = (
( (defined $query->param('killreason'.$nid)) && (length($r=$query->param('killreason'.$nid))!=0) )
	? $r.' '
	: ''
	);

#new way:
#	$HTMLVARS{msgparams} = {
#		'author_id'=>$Kid,
#		'recipient_id'=>$aid,
#		'message'=> '[Node Heaven] will become the new residence of your writeup I ate, ['.$nt.']. '.$m,
#	};
#	$HTMLVARS{msgparams}{fromgroup_id} = $UID if ( (defined $query->param('publickiller'.$nid)) && ($query->param('publickiller'.$nid)) );
#	$HTMLVARS{msgparams}{ccself} = 1 if ( (defined $query->param('ccmeklap'.$nid)) && ($query->param('ccmeklap'.$nid)) );
#	htmlcode('sendPrivateMessage','');


#old way:
#2007-11-21: removed klaproth -- ascorbic
	my $msgHash = {
		msgtext=>'I deleted your writeup ['.$nt.']. '.$m.'[Node Heaven] will become its new residence.',
#		author_user=>$Kid,
		author_user=>$UID,
		for_user=>$aid,
	};
#	$$msgHash{for_usergroup} = $UID if ( (defined $query->param('publickiller'.$nid)) && ($query->param('publickiller'.$nid)) );
	$DB->sqlInsert('message', $msgHash);


#old way:
#	my $isAnon = defined $$msgHash{for_usergroup} ? '' : ' anonymously';
	my $aut = getNodeById($aid);
	$DB->sqlInsert('seclog', {'seclog_user' => $UID, 'seclog_node' => $$mass{node_id}, 'seclog_details' => "[$nt] by [$$aut{title}] was killed: $m"});


##new way: (for testing only, so far; have to fix some things)
