node.code:
return if $APP->isGuest($USER);

my $UID = getId($USER)||0;
my $isRoot = $APP->isAdmin($USER);

foreach($query->param) {
	if($_ =~ /^deletemsg\_(\d+)$/) {

		# Delete a message given param named : deletemsg_<messageid>
		my $MSG = $DB->sqlSelectHashref('*', 'message_outbox', "message_id=$1");
		next unless $MSG;
		next unless $isRoot || ($UID==$$MSG{author_user});
		$DB->sqlDelete('message_outbox', "message_id=$$MSG{message_id}");
	} elsif($_ =~ /^archive\_(\d+)$/) {

		# Archive a message given param named : archive_<messageid>
		my $MSG = $DB->sqlSelectHashref('*', 'message_outbox', "message_id=$1");
		next unless $MSG;
		next unless $isRoot||($UID==$$MSG{author_user});
		my $realTime = $$MSG{tstamp};
		$DB->sqlUpdate('message_outbox', {archive=>1, tstamp=>$realTime}, 'message_id='.$$MSG{message_id});

	} elsif($_ =~ /^unarchive\_(\d+)$/) {

		# Un-archive a message given param named : unarchive_<messageid>
		my $MSG = $DB->sqlSelectHashref('*', 'message_outbox', "message_id=$1");
		next unless $MSG;
		next unless $isRoot||($UID==$$MSG{author_user});
		my $realTime = $$MSG{tstamp};
		$DB->sqlUpdate('message_outbox', {archive=>0, tstamp=>$realTime}, 'message_id='.$$MSG{message_id});
	}
}
