node.code:
return unless exists($HTMLVARS{'group_gods'}{$USER->{node_id}})
	|| exists($HTMLVARS{'group_content editors'}{$USER->{node_id}});

my $N = getNodeById($query->param('node_id'));

return unless $N;
return unless $$N{type}{title} eq "e2node";

if($query->param("unlock")){
  $N->{orderlock_user} = 0;
}else{
  $N->{orderlock_user} = $USER->{node_id};
}

updateNode($N, -1);
return;
