node.code:
my $POLL = $query->param("e2poll_id");

getRef( $POLL );

my ($count, $optionstr) = (0, "");

local *filterSub = sub {
   for (@_) {

      s/\"/ \\\"/g;

      return $_;

   }

};

for (1 .. 12) {

  my $queryVal = $query->param("option" . $_);

  if (defined($queryVal) && $queryVal) {

     $optionstr .=$count.' => "'.filterSub($queryVal).'", ';
     $count++;

  }

};

$optionstr.=$count.' => "None of the above."';
$count++;

my $pollquestion = $query->param("poll_question");

if (defined($pollquestion) && $pollquestion) {
   $pollquestion = filterSub($pollquestion);
}


my $str = '
[%

my $question="<p>'.$pollquestion.'</p>";
my %options=('.$optionstr.');
$HTMLVARS{options}={'.$optionstr.'};
my @values=();
my $key;
foreach $key (sort {$a <=> $b} (keys(%options))) {
	push @values, $key;
}

if (!$HTMLVARS{hasvoted}) {
	my $radioname="poll__'.$$POLL{node_id}.'";
	$question.=$query->radio_group(
-name=>$radioname,
-values=>\@values,
-default=>0,
-linebreak=>"true",
-labels => \%options);
}

return $question;
%]';

my $optstr=$$POLL{e2poll_results};
my $normalVotes = substr($optstr,0,rindex($optstr,","));
my $notaVote = substr($optstr,rindex($optstr,",")); #None of the Above
my $count2=0;

my @personal = split(/,/, $optstr);
my $optsize = @personal;
for ($count2=$optsize;$count2<$count;$count2++) {
$normalVotes.=",0";
}
$optstr = $normalVotes.$notaVote;
$$POLL{e2poll_results}=$optstr;

$$POLL{doctext}=$str;
updateNode( $POLL, -1 );
