node.code:
return if ($$VARS{GPoptout});

my $minLevel = 11;
my $Sanctificity = 10;
return unless getLevel($USER)>= $minLevel;

my $U = $query->param('node_id');
$U = getNode($query->param("node"), 'user') if ($query->param('node'));
getRef $U;

return unless $$U{type}{title} eq 'user';

        $$U{sanctity} += 1;
        updateNode($U, -1);

        use Everything::Experience;
        adjustGP($U, $Sanctificity);
        adjustGP($USER, -$Sanctificity);
        $$VARS{oldGP} = $$USER{GP};

$DB->sqlInsert('seclog', {'seclog_user' => $$USER{node_id}, 'seclog_node' => getId(getNode('Sanctify user', 'superdoc')), 'seclog_details' => "$$USER{title} sanctified $$U{title} with $Sanctificity GP."});

	my $from =  "!";
	$HTMLVARS{msgparams} = {
		'author_id' => getId(getNode('Cool Man Eddie', 'user')),
		'recipient_id' => $$U{user_id},
		'message' => "Whoa! You’ve been [Sanctify|sanctified]$from"
	};
	htmlcode('sendPrivateMessage','');
