node.code:
return unless(isGod($USER));

my $uid = $query->param('lock_id');
return unless $uid;
$uid = getNodeById($uid);

return unless($$uid{type_nodetype} == getId(getType('user')));
$$uid{acctlock} = 0;
updateNode($uid, -1);

$APP->securityLog(getNode("unlockaccount","opcode"), $USER, "$$uid{title}'s account was unlocked by $$USER{title}");
