node.code:
return unless(isGod($USER));

my $uid = $query->param('lock_id');
return unless $uid;
$uid = getNodeById($uid);

return unless($$uid{type_nodetype} == getId(getType('user')));
$$uid{acctlock} = 0;
updateNode($uid, -1);

my $OP = getId(getNode("unlockaccount","opcode"));

$DB->sqlInsert('seclog', {'seclog_user' => $$USER{node_id}, 'seclog_node' => $OP, 'seclog_details' => "$$uid{title}'s account was unlocked by $$USER{title}"});
