node.doctext:
[%

####################################################################
# get all the URL parameters

my $wuType = abs int($query->param("wutype"));

my $count = abs int($query->param("count"));
$count = 50 if !$count;

my $page = abs int($query->param("page"));

my $orderby = $query->param('orderby') || "node.reputation DESC";

my $year= $query->param('year') || "2014";

my $nextyear = $year + 1  || "2015";

####################################################################
# Form with list of writeup types and number to show

my (@WRTYPE) = getNodeWhere({type_nodetype => getId(getType('writeuptype'))});
my %items;
map $items{$$_{node_id}} = $$_{title}, @WRTYPE;

my @idlist = sort { $items{$a} cmp $items{$b} } keys %items;
unshift @idlist, 0;
$items{0} = 'All';

my $choices = [
	'cooled DESC,node.reputation DESC', 'node.reputation DESC', 'publishtime DESC', 'publishtime ASC'
];

my $labels = {
        'cooled DESC,node.reputation DESC'=>'C!, then reputation',
        'node.reputation DESC'=>'Reputation',
        'publishtime DESC'=>'Date, most recent first',
        'publishtime ASC'=>'Date, most recent last'
};

my $str = htmlcode('openform')
	.qq'<fieldset><legend>Choose...</legend>
	<input type="hidden" name="page" value="$page">
	<strong>Year:</strong>'
	.$query -> textfield('year', '2014', 4, 4)
	.'
	<label> &nbsp; <strong>Select Writeup Type:</strong>'
	.$query -> popup_menu('wutype', \@idlist, 0, \%items)
	.'</label> &nbsp; 
	<label> &nbsp; <strong>Number of writeups to display:</strong>'
	.$query -> popup_menu('count', [0, 15, 25, 50, 75, 100, 150, 200, 250, 500], $count)
	.'</label> &nbsp;
	<label> &nbsp; <strong>Order By:</strong>'
	.$query -> popup_menu('orderby', $choices, 'cooled DESC,reputation DESC', $labels)
	.'</label> &nbsp; '
	.$query -> submit('Get Writeups')
	.'</fieldset></form>';

####################################################################
# get writeups
#
my $where = "wrtype_writeuptype=$wuType
	and " if $wuType;

$where .= "publishtime >= '".$year."-01-01 00:00:00' and publishtime < '".$nextyear."-01-01 00:00:00'";

my $wus = $DB -> sqlSelectMany('
 	writeup_id, parent_e2node, publishtime,
	node.author_user,
	type.title AS type_title,
	cooled, node.reputation
','
	writeup
	JOIN node ON writeup_id = node.node_id
	JOIN node type ON type.node_id = writeup.wrtype_writeuptype
',
	$where
,'
	ORDER BY '.$orderby.' LIMIT '.($page * $count).','.$count
);

####################################################################
# display
#

$str .= '<table style="margin-left: auto; margin-right: auto;">
	<tr>
	<th>Title</th>
	<th>Author</th>
	<th>Published</th>
	<th>C/rep</th>
	</tr>'
	.htmlcode('show content', $wus, '<tr class="&oddrow">"<td>", parenttitle, type,
		"</td><td>", author, "</td><td align=\'right\'><small>", listdate, "</small></td>
		<td><small>", cooled, "/", reputation, "</small></td>"')
	.'</table>';

$str .= '<p class="morelink">';
$str .= linkNode($NODE, '&lt&lt Prev',
	{wutype => $wuType, page => $page-1, count => $count}).' | ' if $page;

$str .= '<b>Page '.($page+1).'</b> | '
	.linkNode($NODE, 'Next &gt;&gt;',
		{wutype => $wuType, page => $page+1, count => $count})
	.'</p>';

return $str;
%]
