node.doctext:
[%

my $start = int($query->param('start'));
$start ||= 0;

#$sql = "select `user_id`, `nick`, `createtime`, `lasttime` from `user`, `node` where `node_id`=`user_id` and `createtime` < date_sub(now(), interval 10 year) order by createtime asc limit $start, 50";
$sql =  "select `user_id`, `nick`, `createtime`, `lasttime`, `notetext` from `node`, `user` left join `nodenote` on (`nodenote_nodeid` = `user_id`) where `node_id`=`user_id` and `createtime` < date_sub(now(), interval 119 month) group by (user.user_id) order by lasttime desc limit $start, 200";

$usr = $dbh->prepare($sql);

$usr->execute or return 'Oh dear. Database oops';

my $row = 1;
my $last = $start + 50;

my $str = "<p>This rather specialised document is to determine who's still here, having stuck around for almost a decade (this being motivated by E2's decaversary celebrations)...</p>";
my $nav = "<p style='text-align: center'>";

if ($start > 0) {
   $nav .= '&laquo ' . linkNode($NODE, 'Later', {'start' => $start - 50}) . ' ';
}

for (my $i = 0; $i<$numUser; $i += 50) {   
   $nav .= ($i == $start) ? "<b>$i</b>" : linkNode($NODE, $i ? $i : 1, {'start' => $i});
   $nav .= ' ';
}

if ($last < $numUser) {
   $nav .= linkNode($NODE, 'Earlier', {'start' => $start + 50}) . ' &raquo;</p>';
}

$str .= $nav;

$str .= "<table style='width: 100%; border-top: 1px gray solid'><tr><th>Joined</th><th>User</th><th>Last logged in</th></tr>\n";
$usr->fetchrow_hashref;
while(my $N = $usr->fetchrow_hashref) {
	my $login = htmlcode('timesince',$$N{lasttime});
   my $create = htmlcode('timesince',$$N{createtime});

   $str .= sprintf("\t<tr class='%s'><td>%s</td><td><a href=%s>%s</a></td><td>%s</td></tr>\n",
                  $row ? 'oddrow' : 'evenrow',
                  $create,
                  urlGen({'node_id'=>$$N{user_id}}),
                  $$N{nick},
                  $login eq '?' ? 'never' : $login,
                  );
   $row= !$row;
}
$str .= '</table>' . $nav;
$str;
%]
