node.doctext:
[{openform}]
User [% $query->textfield("myuser", "", 30) %]
gets [% $query->textfield("mycools", "", 3, 3) %]
cools.
[{closeform}]
<p>
[%
my $user = $query->param("myuser");
my $cools = $query->param("mycools");

return unless $APP->isAdmin($USER);

return unless $user and $cools;
return "invalid cools" unless $cools =~ /^\d+$/;


my $U = getNode($user, 'user');
return "<i>No user \"$user\" exists!</i>" unless $U;

my $V = getVars $U;
$$V{cools} = $cools;
setVars($U, $V);
"<i>$cools cools were bestowed to ".linkNode($U)."</i>";
$APP->securityLog($NODE, $USER, "$$U{title} was given $cools cools by $$USER{title}");
$$U{karma}+=1;
updateNode($U, -1);

return 'Success!';
%]
