node.doctext:
[{openform}]

[%
my $SUID = getId($USER)||0;

my $victim = $query->param('usersearch');
$victim = $$USER{title} unless ($victim);

if ( $APP->isAdmin($USER) ) {
    		$query->textfield('usersearch', $victim);
	} else { 
		$query->hidden('usersearch',$victim);  
}
%]


[%

#
#  Again, Gorgonzola takes perfectly good E2 code and twists it
#  to his nefarious purposes
#



#N-Wing converted this from FormMenu to plain old CGI so items could be in a logical order

#this sets the ordering of items in the combo box
my $choices = [
	'title ASC', 'wrtype_writeuptype ASC,title ASC'
];

my $labels = {
	'title ASC'=>'Title',
	"wrtype_writeuptype ASC,title ASC"=>"Writeup type, then title",
};

my $raw = $query->param('raw');
my $fstr = $query->hidden('filterhidden');
$fstr .= "Order By:".$query->popup_menu('orderby', $choices, 'title ASC', $labels);
$fstr .= "<br />".$query->checkbox(-name=>'raw', -label=>'Raw Data', -checked=>$raw);
my $fdelim = $query->param('delimiter');
$fdelim = "_" unless ($fdelim);
$fstr .= "&nbsp;&nbsp;Delimiter: ".$query->textfield('delimiter',$fdelim)."<br />";
return $fstr;
%]

[{closeform}]

[%

my $UID = $$USER{node_id} || 0; #uppercase = user that is viewing
my $isRoot = $APP->isAdmin($USER);
my $us;
my $israw = $query->param('raw');

#if ($isRoot) { 
    $us = $query->param('usersearch'); #user's title to find WUs on
#   } else {
#    $us = $$USER(TITLE);
#};
my $orderby = $query->param('orderby');
my $delim = $query->param('delimiter');

return '' unless($orderby);
my $orderdata = {
	'title ASC'=>1,                        # Title
	'wrtype_writeuptype ASC,title ASC'=>1, # Writeup type, then title
};
$orderby = '' unless exists $$orderdata{$orderby};

#NOTE:  we must CHECK to make sure orderby is one of 
#our valid options, otherwise a user could potentially
#execute arbitrary SQL -- VERY BAD

$orderby ||= 'title ASC';

if ($israw) {
   return "Delimiter (".$delim.") must be exactly one character." unless (length($delim)==1);
};   

#return "Order by: ".$orderby;

#quit if no user given to get info on
return 'It helps to give a user\'s nick.' unless $us;

#quit if invalid user given
my $user = getNode($us,'user');
return 'It seems that the user "'.$us.'" doesn\'t exist... how very, very strange... (Did you type their name correctly?)' unless(defined $user);
return 'Are you really looking for almost all the words in the English language?' if $$user{title} eq 'Webster 1913';

#constants setup
my $uid = getId($user) || 0;	#lowercase = user searching on

my $isMe = ($uid == $UID) && ($uid!=0);
my $rep = $isMe || $isRoot;

my $str = '';

my $isEd = $rep || $APP->isEditor($USER);

#quit for special bots

return '<p align="center"><big><big><strong>G r o w l !</strong></big></big>' if ($$user{title} eq 'EDB');


#load writeup information


#database setup
my $qh;	#query handle
my $typeID = getId(getType('writeup')) || 0;

#
# total writeup count
#

$qh = $DB->{dbh}->prepare('SELECT COUNT(*) FROM node WHERE author_user='.$uid.' AND type_nodetype='.$typeID);

$qh->execute();
my ($totalWUs) = $qh->fetchrow();
$qh->finish();

return linkNode($user) . ' has no writeups'.'.' unless $totalWUs;

#load in only writeups we're currently looking at
$qh = $DB->{dbh}->prepare(
#
# Why select all columns?
#
#	'SELECT parent_e2node, title, cooled, reputation, createtime','wrtype_writeuptype'
	'SELECT *'.
        ' FROM node,writeup WHERE node.author_user='.$uid.
	' AND node.type_nodetype='.$typeID.
	' AND writeup.writeup_id=node.node_id'.
	' ORDER BY ' .$orderby#.
#	' LIMIT 1,50'  #comment this out after debug
);
$qh->execute();	#gets current WUs and their info
my @allWUInfo;
while(my $r=$qh->fetchrow_hashref) {
	push(@allWUInfo, $r);
}
$qh->finish();


#done with getting writeup info, the rest is just display
#(except for special cases, like showing what is killed)





$str .= ($totalWUs==1 ? 'This writeup was' : 'These '.$totalWUs.' writeups were all') . ' written by ' . linkNode($user, ($isMe?'you':0), {lastnode_id=>0}) . 

":</p>\n";

#prepare for loop
my $nr = getId(getNode('node row', 'superdoc'));
my $kuid;
my $drn=0;	#display row number - for row coloring
my $wuid;	#current WU's ID

#loop through WUs, and show their info
if ($israw) {
        $str.="<pre>";
	foreach my $wu (@allWUInfo) {

		$wuid = getId($wu);

		$str .= $$wu{title}.$delim;

                if ($$wu{cooled}) {
                   $str .= "$$wu{cooled}C!".$delim;
                   } else {
                   $str .= " ".$delim;
                };

		if($rep) {
                        $str .= $$wu{reputation}.$delim;

			#N-Wing TODO? do this correctly, using 2 queries (although a bit slower)
			my ($votescast) = $DB->sqlSelect('count(*)', 'vote', 'vote_id='.$wuid);

			$str .= $votescast .$delim;
		}

		$str .= htmlcode('parsetimestamp', "$$wu{createtime}")."\n";
	}
        $str .= "</pre>";

} else { 

#header

	$str .= '<table border="0" cellpadding="1" cellspacing="0">'."\n".
		'<tr><th align="left">Writeup Title (type)</th><th>C!</th>';
	$str .= '<th colspan="2" align="center">Rep</th>' if $rep;
	$str .= '<th align="center">Create Time</th>';
	$str .= "</tr>\n";
	foreach my $wu (@allWUInfo) {

		$wuid = getId($wu);

		$str .= '<tr';
		$str .= ' class="oddrow" bgcolor="#bbbbff"' unless ($drn%2);
		$str .= '>';

		$str .= '<td nowrap>'. linkNode($$wu{parent_e2node}, $$wu{title}, {lastnode_id=>0}).'</td><td>';
		$str .= " <strong>$$wu{cooled}C!</strong>&nbsp;" if $$wu{cooled};
		$str .= '</td>';

		if($rep) {
			my $r = $$wu{reputation} || 0;

			#N-Wing TODO? do this correctly, using 2 queries (although a bit slower)
			my ($votescast) = $DB->sqlSelect('count(*)', 'vote', 'vote_id='.$wuid);
			my $p = ($votescast + $r)/2;
			my $m = ($votescast - $r)/2;
			$str .= '<td>'.$r.'</td><td><small>+'.$p.'/-'.$m.'</small></td>';
		}

		$str .= '<td nowrap align="right"><small>'.htmlcode('parsetimestamp', "$$wu{createtime}")
        	       ."</small></td></tr>\n";

		++$drn;
	}
        $str .= "</table>\n";
}




$str;
%]
