node.doctext:
This is what the cache contains<br>
[% "(Process ID: $$)\n"; %]
<p>
[%

my $cache = $DB->getCache()->dumpCache();
my $num = $DB->getCache()->getCacheSize();
my $str = "Cache size: $num\n";
my $item;

$str .= "<ul>\n";

my $typestats;

foreach $item (@$cache)
{
  $typestats->{$item->{type}->{title}} ||= 0;
  $typestats->{$item->{type}->{title}}++;

  $str .= "<li> $$item{title} (" . $$item{type}{title} .
    ")\n";
}

$str .= "</ul><br /><br />Counts: <ul>";

foreach my $key(keys %$typestats)
{
  $str .= "<li>$key: ".$typestats->{$key}
}
$str .= "</ul>";

$str;
%]
