node.doctext:
Your fellow noders:<br>
[%

my $time = 3 * 60;
my $limit = 30;

my $csr=$DB->getDatabaseHandle()->prepare("	
  SELECT node_id, title, experience FROM node, user
  WHERE node_id=user_id and
  type_nodetype=" . getId ($DB->getType('user')) .
  " and (UNIX_TIMESTAMP(now())-UNIX_TIMESTAMP(lasttime) < $time) ORDER BY experience DESC LIMIT $limit");

$csr->execute or return "SHIT";
my $str;
while(my $U = $csr->fetchrow_hashref) {
	$str .= linkNode($U) ."<br>";
}
$csr->finish;
return $str if $str;
"<i>no users logged in</i>";
%]
