node.doctext:
[%

my $explaintest = 0;
my $testexplainoutput;

my $current_node_id = 940175; # February 5, 2001 - a known "atleast" containing node
if ($query->param('fix'))
	{ $current_node_id = $query->param('lastdisplayednode'); }
my $current_node = getNodeById($current_node_id);
my $notes = '<ol><b>Temporary programming notes:</b>';
# This default value will not be necessary after the sql query
# is fixed to not immediatly run on page load
my $findword = $query->param('findword') || 'root beer';
my $replaceword = $query->param('replaceword') || 'the';
my $ignorestring; # and node.node_id != 940175 and node... etc
my $textfields = '';
# copy ignoreids list from previous iterations
my $ignoreids = $query->param('ignoreids');
if ($query->param('skip'))
	{ $ignoreids .= ' ' . $query->param('lastdisplayednode'); }
my $comments = "<p>This is a prototype find/replace typo fixer.  "
		. "It does not currently make any changes to writeups.  "
		. "Submit searches for nodes containing Find and highlights them in the display below.  "
		. "Skip re-runs the search skipping that node, "
		. "the page keeps track of all the nodes that have been skipped.  "
		. "Fix does not yet fix anything, it just sends you a message.  "
		. "This message is what the noder will receive after the fix function is implemented.</p>";

# Clean up the list of ignored node_ids
# TODO: make sure the list is valid, numbers separated by spaces only
$ignoreids =~ s/^\s+//; # remove any leading whitespace
$ignoreids =~ s/\s+$//; # remove any trailing whitespace

# Our list of ids to ignore is separated by spaces
# Split them into a temporary array and then join them
#   in the correct syntax to put them into the sql query
if ($ignoreids =~ /\d+/)
	{
	my @tempignorestring = split(/\s+/, $ignoreids);
	$ignorestring = 'and node.node_id != ' 
				. join(' and node.node_id != ', @tempignorestring);
	$notes .= "<li>$ignorestring</li>";
	}
if ($explaintest == 0)
{
$notes .= "<li>Got here</li>";
	#####
	# Select nodes that are writeups where the node_id is not ignored
	#   and the findword is in the node (note: may be part of a word!)
	# Use with caution, this query has the potential to run for a long time.
	# TODO: run the search only when the "submit" or "skip" button is pushed
	#   Do not run if the ignorestring is invalid or empty
	#   If the "fix" button was pushed just re-display the fixed writeup
	#   What happens if it doesn't return anything?

#	if ($query->param('submit') || $query->param('skip'))
	if ($query->param('submit'))
		{
$notes .= "<li>Got here too</li>";
# 		my $csr = $DB->sqlSelectMany
# 			(
# 			"*", 
# 			"node JOIN document ON node.node_id=document.document_id", 
# 			"type_nodetype=117 
# 				$ignorestring
# 				and document.doctext REGEXP ('.$findword.')",
# 			"LIMIT 1"
# 			);
		my $csr = $DB->sqlSelectMany
			(
			"*", 
			"node JOIN document ON node.node_id=document.document_id", 
			"type_nodetype != 117 
 			AND type_nodetype != 15
 			AND type_nodetype != 116
 			AND type_nodetype != 148
 			AND type_nodetype != 153
				and document.doctext REGEXP ('.$findword.')",
			"LIMIT 1"
			);

		while (my $s = $csr->fetchrow_hashref)
			{
			$notes .= "<li>" . linkNode($$s{'node_id'}, $$s{'title'});
			$notes .= "</li>";
			$current_node_id = $$s{'node_id'}; # grabs the last one
			}

		$current_node = getNodeById($current_node_id);

		$notes .= "<li>Current node_id = " 
			. $current_node_id 
			. " by "
			. getNodeById($$current_node{author_user})->{title}
			. "</li>";
		}

	#####
	# Fix the node if the fix button was pushed
	# use a regexp to find $findword, replace with $replaceword
	# send a message to the noder that his writeup was auto-fixed
	# redisplay fixed writeup rather than run a new search
	if ($query->param('fix'))
		{
		my $writeuptitle = getNodeById($current_node_id)->{title};
		my $messagetext = "[$$USER{title}] fixed your writeup [$writeuptitle], "
				. "all instances of \"$findword\" have been replaced with \"$replaceword\".";

		htmlcode('sendPrivateMessage',{
			'author_id' => getId(getNode('Klaproth', 'user')),
			'recipient_id' => $$USER{author_user},
			'message' => $messagetext,
			});

	#	htmlcode('sendPrivateMessage',{
        #		'author_id' => getId(getNode('Klaproth', 'user')),
	#		'recipient_id' => $$current_node{author_user},
	#		'message' => $messagetext,
	#		});
		}

	#####
	# Format the writeup for display
	# Should this show the HTML?  Could be useful for fixing HTML errors
	#   If so, display with extra P tags in front of the literalized P or BR or PRE
	# Should this show the hard link brackets?
	my $writeup_text = '<div style="border: 4px dotted black; padding: 20px;">' 
					. $$current_node{'doctext'} 
					. '</div>';

	$notes .= "<li>Nodetype: " . $$current_node{'type_nodetype'} . "</li>";

	#####
	# Highlight find and replace words
	# TODO: protect against conflicts such as $findword eq $replaceword
	#   or one word contained within the other
	$writeup_text =~ s/($findword)/<span style=\"background:yellow;\">$1<\/span>/gi;
	$writeup_text =~ s/($replaceword)/<span style=\"background:orange;\">$1<\/span>/gi;

	#####
	# Create textfields for the interface
	# Would ignoreids be better as a textarea?
	#   In testing it seems so but in practice you probably won't ignore more than a few
	$textfields .= htmlcode('openform');
	$textfields .= $query->textfield('findword') . " Find <br>";
	$textfields .= $query->textfield('replaceword') . " Replace <br>";
	$textfields .= '<input type="text" name="ignoreids" size="100" value="'
				   . $ignoreids 	
				   . '"> Skip these nodes<br>';
	$textfields .= '<input type="submit" name="submit" value="submit" /> ';
	$textfields .= '<input type="submit" name="skip" value="skip"> ';
	$textfields .= '<input type="submit" name="fix" value="fix"> ';
	$textfields .= '<input type="hidden" name="lastdisplayednode" value="'
				   . $current_node_id
				   . '">';
	$textfields .= '</form>';

	#####
	# Display page - $notes does not have its own /ol tag

	return $comments . $textfields . $notes . "</ol>" . $writeup_text;
	}
else
	{
# 			(
# 			"*", 
# 			"node JOIN document ON node.node_id=document.document_id", 
# 			"type_nodetype=117 
# 				$ignorestring
# 				and document.doctext REGEXP ('.$findword.')",
# 			"LIMIT 1"
# 			);

# 	my $csr = $DB->{dbh}->prepare
# 		("EXPLAIN SELECT *
# 		FROM node JOIN document ON node.node_id = document.document_id
# 		WHERE type_nodetype=117
# 			$ignorestring
# 			AND document.doctext REGEXP ('.$findword.')
# 		LIMIT 1");
# Exclude: 15 user 116 e2node 117 writeup 148 dbtable 153 setting
	my $csr = $DB->{dbh}->prepare
		("EXPLAIN SELECT *
		FROM node JOIN document ON node.node_id = document.document_id
		WHERE type_nodetype != 117
			AND type_nodetype != 15
			AND type_nodetype != 116
			AND type_nodetype != 148
			AND type_nodetype != 153
			AND document.doctext REGEXP ('.$findword.')
		LIMIT 1");
	$csr->execute();
	while (my $s = $csr->fetchrow_hashref)
		{
		foreach my $keys (sort(keys(%{$s}))) 
			{ $testexplainoutput .= "\n\t<li>" . $keys . ": " . $s->{$keys} . "</li>"; }
		}
	return $testexplainoutput . "</ol>";
	}

%]
