node.doctext:
[%
my $limit = 15;

my $str = "<table width=100% border=0 cellpadding=0 cellspacing=0>";
my $batch = $DB->sqlSelect("max(batch)", "newnodes");

my $csr = $Everything::dbh->prepare("
	SELECT * FROM newnodes
        left join node on newnodes.node_id=node.node_id
	where batch = $batch
	order by newnodes.id
	LIMIT $limit");

$csr->execute or return "SHIT";

my $count=0;
my @colors= split "\s*,\s*",$$THEME{themeColors} if exists $$THEME{themeColors};
@colors = ("#CCCC99") unless @colors;
while(my $N = $csr->fetchrow_hashref){
  $N = getNodeById($$N{node_id});
  my $PN = getNodeById($$N{parent_e2node});
  my $WR = getNodeById $$N{wrtype_writeuptype};
  my $clr = $colors[$count++%int(@colors)];
  my $st = $$PN{title} if(ref $PN eq "HASH");
  my $len = 24;
  my @words = split ' ', $st;

  foreach (@words)
  {
    if(length($_) > $len)
    {
      $_ = substr($st, 0, $len);
      $_ .= "...";
    } 
  }

  $st = join ' ', @words;

  $str .= "<tr bgcolor=$clr><td align=center colspan=2><b><font size=2>".linkNode($$N{author_user}, "", {lastnode_id=>0})."</font></b></td>\n";
  $str .= "</tr><tr><td align=left><font size=2>".linkNode($$N{parent_e2node}, $st, {lastnode_id => 0 }) 
  ."</font></td>\n<td align=right><font size=2><i>($$WR{title})</i>"
  . "</font></td></tr>\n";	
}
$str.="</font></td></tr></table>\n";
$csr->finish;
$str;


%]
