for (my $i = 0; $i < $count; $i++) {
  $query->param("tokenateUser$i", '');
  $str.="\n\t<tr><td>";
  $str.=$query->textfield("tokenateUser$i", '', 40, 80);
  $str.="</td>";
}

$str.='</table>';

$str.=htmlcode('closeform');

