node.doctext:
[%

return unless $$USER{title} eq 'mauler';


my $str;

$str.=$query->start_form();
$str.=$query->hidden('node_id', $$NODE{node_id});
$str.=$query->submit('Unrecalculate','Unrecalculate!');
$str.=$query->end_form();

if ($query->param('Unrecalculate')) {

$$VARS{hasRecalculated} = 0;
setVars $USER, $VARS;
$str.= "<p>Unrecalculations complete!</p>";
return $str;      

} else {

return $str;

}

%]
