node.doctext:
[{openform}]
from backup, node_id: <input type="text" name="sql_id">
<input type="submit" value="fix">
</form>
<br><br>
[%
  my $sqlid = $query->param('sql_id');
  return "ZAP!" unless($sqlid);
  getRef($sqlid);

  return "No doctext!<br><br>" unless($$sqlid{doctext});
  $$sqlid{doctext} =~ s/\\\'/\'/g;
  $$sqlid{doctext} =~ s/\\\"/\"/g;

  updateNode($sqlid, -1);
  return "Done";
%]
