<input type="text" name="audituser">
[{closeform}]

[%
return "Which user should we audit?" unless ($query->param('audituser'));
my $usr = getNode($query->param('audituser'), 'user');
return "Not a user!" unless($usr);


my $votenum = 0;
my $str = '';
my $wucount = $DB->sqlSelect("count(*)", "node", "type_nodetype=117 AND author_user=$$usr{user_id} LIMIT 15");

return "User ".linkNode($usr, $$usr{title})." has no writeups" unless($wucount > 0);

my $csr = $dbh->prepare("SELECT node_id FROM node WHERE type_nodetype=117 AND author_user=$$usr{user_id} LIMIT 15");

$csr->execute();

while(my $row = $csr->fetchrow_hashref())
{

my $WRITEUP = getNodeById($$row{node_id});
next unless($WRITEUP);
my $timestamp = $$WRITEUP{publishtime};

#if(exists $HTMLVARS{group_gods}{getId($USER)} or exists $HTMLVARS{'group_content editors'}{getId($USER)}) {
return htmlcode('displayWriteupInfo',$votenum.','.$$VARS{wuhead});
#}

my $TYPE = $$WRITEUP{wrtype_writeuptype};
getRef $TYPE;
$str = '<strong>(' . linkNode($WRITEUP, $$TYPE{title}) . ') by&nbsp;' . linkNode($$WRITEUP{author_user}) . '</strong> (' . linkNode($WRITEUP, 'print', {displaytype=>'printable', lastnode_id=>0}) . ')';
#$str .= '('.htmlcode('printable','print').')'; #doesn't work right in e2node

if($timestamp) {

my $tableRowOpen = '<table cellpadding="0" cellspacing="0" border="0" width="100%"><tr>';
my $tableRowClose = "</tr></table>\n";
$str = $tableRowOpen . '<td>'.$str.'</td><td align="center">' . htmlcode('voteit', $votenum);

#$str.=linkNode(getNode('node row', 'superdoc'), '<strong>X!</strong>', {alt='this node is marked for deletion'})) if $$USER{title} eq 'nate';

# $str .= htmlcode('CoolUncoolIt',$votenum);

$str .= '</td><td align="right"><small>' . htmlcode('parsetimestamp', "$$WRITEUP{publishtime}") . '</small></td>'.$tableRowClose.$tableRowOpen;

$str .= '<td align="center">' . htmlcode('writeupcools',$$WRITEUP{node_id}
#.',1' #uncomment this line to only show # of C!s
).'</td>'.$tableRowClose;

}

}
return $str;

%]
